import React, { Component } from "react";
import { Card, Button } from "react-bootstrap";
import img from "../../src/Krish.png";

class Counter extends Component {
  styles = {
    fontSize: 15,
    fontWeight: "bold",
  };

  render() {
    return (
      <Card bg="white" variant="dark" style={{ width: "18rem" }}>
        <Card.Img variant="top" style={{ height: 250, width: 250 }} src={img} />
        <Card.Body>
          <Button
            variant="primary"
            onClick={() => this.props.onIncrement(this.props.counter)}
          >
            Increment
          </Button>
          <Button
            variant="primary"
            onClick={() => this.props.onDecrement(this.props.counter.id)}
          >
            Decrement
          </Button>
          <Card-Text>Score: {this.props.counter.score}</Card-Text>
        </Card.Body>
      </Card>
    );
  }
}

export default Counter;
