import React, { Component } from "react";
import { Card, Button, Row, Col } from "react-bootstrap";
import img from "../../src/Krish.png";

class Counter extends Component {
  styles = {
    fontSize: 15,
    fontWeight: "bold",
  };

  render() {
    return (
      <Card
        bg="dark"
        variant="dark"
        text="light"
        style={{ width: "18rem", margin: "2%" }}
      >
        <Card.Img
          variant="top"
          style={{ height: 250, width: "100%" }}
          src={img}
        />
        <Card.Body>
          <Row>
            <Col>
              <Button
                variant="primary"
                onClick={() => this.props.onIncrement(this.props.counter)}
              >
                Increment
              </Button>
            </Col>
            <Col>
              <Button
                variant="primary"
                onClick={() => this.props.onDecrement(this.props.counter.id)}
              >
                Decrement
              </Button>
            </Col>
          </Row>

          <h5>Score: {this.props.counter.score}</h5>
        </Card.Body>
      </Card>
    );
  }
}

export default Counter;
