    let cardRender = this.props.counters.map((counter) => (
      //<card-group style={{ display: "flex", flexDirection: "row" }}>
      <Grid>
        <Row key={counter.id}>
          <Col sm="6">
            <Card>
              <Card.Title>Card 1</Card.Title>
              <Card.Img
                variant="top"
                style={{ height: 250, width: 250 }}
                src={img}
              />
