                <Button variant="primary">Increment</Button>
                <Button variant="primary">Decrement</Button>
                <Card-Text>Score:</Card-Text>
              </Card.Body>
            </Card>
          </Col>
        </Row>
      </Grid>
      //</card-group>
      // <Card
      //   style={{ width: "18rem" }}
      //   // style={{ width: "18rem", display: "flex", flexDirection: "row" }}
      //   key={counter.id}
      // >
      //   <Card.Title style={{ display: "flex" }}>Proile 1</Card.Title>
      //   <Card.Img variant="top" style={{ height: 250, width: 250 }} src={img} />
      //   <Card.Body style={{ flex: 1 }}>
      //     <Button variant="primary">Increment</Button>
      //     <Button variant="primary">Decrement</Button>
      //     <Card.Text>Score</Card.Text>
      //   </Card.Body>
      // </Card>
      // <Row key={counter.id}>
      //   <Col sm="6">
      //     <Counter
      //       onIncrement={this.props.onIncrement}
      //       onDecrement={this.props.onDecrement}
      //       counter={counter}
      //     />
      //   </Col>
      // </Row>
    ));
    return <Container>{cardRender}</Container>;
  }
}

export default Counters;
