import React, { Component } from "react";
import Counters from "./components/Counters";
import axios from "axios";
import { Navbar, Nav } from "react-bootstrap";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faGithub, faLinkedin } from "@fortawesome/free-brands-svg-icons";

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      counters: [],
      //   { id: 1, score: 4 },
      //   { id: 2, score: 3 },
      //   { id: 3, score: 2 },
      //   { id: 4, score: 1 },
      // ],
    };
  }
  componentWillMount = () => {
    axios
      .get("http://localhost:3002/getScores")
      .then((response) => {
        console.log("got scores");
        if (response.status === 200) {
          const counters1 = [
            { id: 1, score: 4 },
            { id: 2, score: 3 },
            { id: 3, score: 2 },
            { id: 4, score: 1 },
          ];

          const counters = [
            { id: response.data[0]._id, score: response.data[0].score },
            { id: response.data[1]._id, score: response.data[1].score },
            { id: response.data[2]._id, score: response.data[2].score },
            { id: response.data[3]._id, score: response.data[3].score },
          ];
          this.setState({ counters });
          console.log(response.data[0].score);
        }
      })
      .catch((err) => {
        console.log(err);
      });
  };

  handleIncrement = (counter) => {
    const counters = [...this.state.counters];
    console.log(counters);
    console.log(counter);

    const index = counters.indexOf(counter);
    counters[index] = { ...counter };
    counters[index].score++;

    axios
      .put("http://localhost:3002/like", counter)
      .then((response) => {
        console.log("inside success");
        console.log("Status Code : ", response.status);
        if (response.status === 200) {
          this.setState({ counters });
        }
      })
      .catch((err) => {
        console.log("In error");
        console.log(err);
      });
  };

  handleDecrement = (counter) => {
    const counters = [...this.state.counters];
    const index = counters.indexOf(counter);
    counters[index] = { ...counter };
    counters[index].score--;
    axios
      .put("http://localhost:3002/dislike", counter)
      .then((response) => {
        console.log("inside success");
        console.log("Status Code : ", response.status);
        if (response.status === 200) {
          this.setState({ counters });
        }
      })
      .catch((err) => {
        console.log("In error");
        console.log(err);
      });
  };

  render() {
    return (
      <>
        <Navbar bg="dark" variant="dark">
          <Navbar.Brand>Nithin Krishna</Navbar.Brand>
          <Nav className="ml-auto">
            <Nav.Link
              href="https://www.linkedin.com/in/nithin-krishna-31624917a/"
              style={{ color: "#FFF" }}
              target="_blank"
            >
              <FontAwesomeIcon
                size="2x"
                color="white"
                icon={faGithub}
                to=""
              ></FontAwesomeIcon>
            </Nav.Link>
            <Nav.Link
              style={{ color: "#FFF" }}
              href="https://www.linkedin.com/in/nithin-krishna-31624917a/"
            >
              <FontAwesomeIcon
                size="2x"
                color="white"
                icon={faLinkedin}
              ></FontAwesomeIcon>
            </Nav.Link>
          </Nav>
        </Navbar>
        <Counters
          counters={this.state.counters}
          onIncrement={this.handleIncrement}
          onDecrement={this.handleDecrement}
        />
      </>
    );
  }
}

export default App;
